import React from "react";
import TableRow from "./TableRow";

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rows: 0,
      cols: 0,
      color: "",
    };

    this.addRow = this.addRow.bind(this);
    this.addCol = this.addCol.bind(this);
    this.removeRow = this.removeRow.bind(this);
    this.removeCol = this.removeCol.bind(this);
    this.setColor = this.setColor.bind(this);
    this.changeColor = this.changeColor.bind(this);
    this.clearAll = this.clearAll.bind(this);
    this.fillAll = this.fillAll.bind(this);
    this.fillUncolored = this.fillUncolored.bind(this);
    this.mouseDown = this.mouseDown.bind(this);
    this.mouseUp = this.mouseUp.bind(this);
    this.mouseEnter = this.mouseEnter.bind(this);
  }

  addRow() {
    this.setState({
      rows: this.state.rows + 1,
    });
  }

  addCol() {
    this.setState({
      cols: this.state.cols + 1,
    });
  }

  removeRow() {
    if (this.state.rows > 0) {
      this.setState({
        rows: this.state.rows - 1,
      });
    } else {
      this.setState({
        cols: 0,
      });
    }
  }

  removeCol() {
    if (this.state.cols > 0) {
      this.setState({
        cols: this.state.cols - 1,
      });
    } else {
      this.setState({
        rows: 0,
      });
    }
  }

  setColor(event) {
    this.setState({
      color: event.target.value,
    });
  }

  changeColor(event) {
    event.target.style.backgroundColor = this.state.color;
  }

  clearAll() {
    document.querySelectorAll("td").forEach((cell) => {
      cell.style.backgroundColor = "";
    });
  }

  fillAll() {
    document.querySelectorAll("td").forEach((cell) => {
      cell.style.backgroundColor = this.state.color;
    });
  }

  fillUncolored() {
    document.querySelectorAll("td").forEach((cell) => {
      if (cell.style.backgroundColor === "") {
        cell.style.backgroundColor = this.state.color;
      }
    });
  }

  mouseDown() {
    this.setState({
      coloring: true,
    });
  }

  mouseUp() {
    this.setState({
      coloring: false,
    });
  }

  mouseEnter(event) {
    if (this.state.coloring) {
      event.target.style.backgroundColor = this.state.color;
    }
  }

  render() {
    let tableRows = [];
    for (let i = 0; i <= this.state.rows; i++) {
      tableRows.push(
        <TableRow
          key={i.toString() + "r"}
          colsNum={this.state.cols}
          changeColor={this.changeColor}
          mouseDown={this.mouseDown}
          mouseUp={this.mouseUp}
          mouseEnter={this.mouseEnter}
        />
      );
    }

    return <div>
            <div className="buttons">
                <button onClick={this.props.addRow} className="btn-success btn-lg">add row</button>
                <button onClick={this.props.addCol} className="btn-success btn-lg">add col</button>
                <button onClick={this.props.removeRow} className="btn-success btn-lg">remove row</button>
                <button onClick={this.props.removeCol} className="btn-success btn-lg">remove col</button>
                <button onClick={this.props.clearAll} className="btn-success btn-lg">clear all</button>
                <button onClick={this.props.fillAll} className="btn-success btn-lg">fill all</button>
                <button onClick={this.props.fillUncolored} className="btn-success btn-lg">fillUncolored</button>
            </div>
            <div className="select-container"> 
                <select onChange={this.props.setColor}>
                    <option value="red">Red</option>
                    <option value="black">Black</option>
                    <option value="green">Green</option>
                    <option value="pink">Pink</option>
                    <option value="purple">Purple</option>
                </select>
            </div>


            <table>
                <tbody>{tableRows}</tbody>
            </table>

        </div>

  }
}

export default Table;
